var feedback=document.querySelector(".feedback-button"),popup=document.querySelector(".feedback-form"),overlay=document.querySelector(".overlay"),close=popup.querySelector(".modal-close"),person=popup.querySelector("[name=person]"),mail=popup.querySelector("[name=mail]"),letter=popup.querySelector("[name=letter]");feedback.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),overlay.classList.add("overlay-show"),person.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-error"),overlay.classList.remove("overlay-show")}),popup.addEventListener("submit",function(e){person.value&&mail.value&&letter.value||(e.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("modal-show")&&(popup.classList.remove("modal-show"),popup.classList.remove("modal-error"),overlay.classList.remove("overlay-show")))});
